select avg(?sum) as ?sum
{
select ?district sum(xsd:decimal(?amount)) as ?sum from <http://linkedspending.aksw.org/ministry_of_health>
{
?obs qb:dataSet ls:ministry_of_health.
?obs lso:ministry_of_health-District ?district.
?obs lso:ministry_of_health-amount ?amount.
?obs lso:refYear ?year.
filter(year(?year)=2014).
}
}
