select ?dep
{ 
 select ?dep sum(xsd:decimal(?amount5)) as ?sum5 sum(xsd:decimal(?amount6)) as ?sum6
 from <http://linkedspending.aksw.org/city-of-springfield-budget>
 {
 ?obs qb:dataSet ls:city-of-springfield-budget.
 ?obs lso:city-of-springfield-budget-to ?dep.
 {
 ?obs lso:city-of-springfield-budget-amount ?amount5.
 ?obs lso:refYear ?year5.
 filter(year(?year5)=2005).
 }
 union
 {
 ?obs lso:city-of-springfield-budget-amount ?amount6.
 ?obs lso:refYear ?year6.
 filter(year(?year6)=2006).
 }
 } 
} group by ?dep having(?sum5>?sum6)
